-- Insert quiz_card entries for all 26 lessons
-- Module 1 (8 lessons)
INSERT INTO lesson_cards (lesson_id, module_id, card_position, card_type, content_json, xp_value) VALUES
('1.1', 1, 18, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 1.1 Quiz","subtitle":"Hazards and Risks","pass_threshold":0.6,"questions":[{"id":"1.1_q1","question":"What is the correct definition of a HAZARD?","options":[{"id":"a","text":"The likelihood of an accident happening"},{"id":"b","text":"Something with the potential to cause harm"},{"id":"c","text":"An accident that has already occurred"},{"id":"d","text":"A safety sign warning of danger"}],"correct":"b","explanation":"A hazard is something with the POTENTIAL to cause harm.","test_tip":"HAZARD = the THING. RISK = what could HAPPEN."},{"id":"1.1_q2","question":"A wet floor on a construction site is an example of:","options":[{"id":"a","text":"A risk"},{"id":"b","text":"A hazard"},{"id":"c","text":"A near miss"},{"id":"d","text":"An accident"}],"correct":"b","explanation":"The wet floor is the hazard. The risk would be slipping."},{"id":"1.1_q3","question":"What does competence mean in health and safety?","options":[{"id":"a","text":"Having worked on site for more than 5 years"},{"id":"b","text":"Having the training, knowledge, and experience to carry out a task safely"},{"id":"c","text":"Being over the age of 18"},{"id":"d","text":"Holding a CSCS card"}],"correct":"b","explanation":"Competence = training + knowledge + experience.","test_tip":"Age or time on site alone does NOT make someone competent."},{"id":"1.1_q4","question":"Which of these is a hazard associated with STORAGE on a construction site?","options":[{"id":"a","text":"A worker not wearing gloves"},{"id":"b","text":"Materials stacked too high that could fall"},{"id":"c","text":"A risk assessment not being completed"},{"id":"d","text":"Heavy rain on site"}],"correct":"b","explanation":"Materials stacked too high is a storage hazard."},{"id":"1.1_q5","question":"What is a near miss?","options":[{"id":"a","text":"An accident that caused minor injury"},{"id":"b","text":"A hazard identified in a risk assessment"},{"id":"c","text":"An event that could have caused injury but did not"},{"id":"d","text":"Working within 2 metres of a hazard"}],"correct":"c","explanation":"Near miss = something NEARLY happened. Report them to prevent future accidents.","test_tip":"Report near misses — they prevent future ACTUAL accidents."}]}', 0),
('1.2', 1, 18, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 1.2 Quiz","subtitle":"Risk Assessments","pass_threshold":0.6,"questions":[{"id":"1.2_q1","question":"What is the FIRST step of a risk assessment?","options":[{"id":"a","text":"Write a method statement"},{"id":"b","text":"Identify the hazards"},{"id":"c","text":"Put up safety signs"},{"id":"d","text":"Buy PPE for workers"}],"correct":"b","explanation":"The first step is always to identify the hazards."},{"id":"1.2_q2","question":"Under which regulation MUST employers carry out risk assessments?","options":[{"id":"a","text":"The Health and Safety at Work Act 1974"},{"id":"b","text":"The Management of Health and Safety at Work Regulations 1999"},{"id":"c","text":"CDM 2015"},{"id":"d","text":"RIDDOR"}],"correct":"b","explanation":"The Management of H&S at Work Regs 1999 is THE regulation for risk assessments.","test_tip":"HASAWA 1974 is the main Act but the Regs give the specific duty."},{"id":"1.2_q3","question":"Who must carry out a risk assessment?","options":[{"id":"a","text":"Any worker on site"},{"id":"b","text":"The youngest worker"},{"id":"c","text":"A competent person"},{"id":"d","text":"The client"}],"correct":"c","explanation":"A competent person must carry out the risk assessment."},{"id":"1.2_q4","question":"How many steps are there in the risk assessment process?","options":[{"id":"a","text":"3"},{"id":"b","text":"5"},{"id":"c","text":"7"},{"id":"d","text":"10"}],"correct":"b","explanation":"There are 5 steps in the risk assessment process."},{"id":"1.2_q5","question":"Which of these would trigger a risk assessment review?","options":[{"id":"a","text":"A worker goes on holiday"},{"id":"b","text":"New equipment is introduced on site"},{"id":"c","text":"The weather improves"},{"id":"d","text":"A new worker joins the team"}],"correct":"b","explanation":"Review when anything changes that could affect safety."}]}', 0),
('1.3', 1, 15, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 1.3 Quiz","subtitle":"Method Statements","pass_threshold":0.6,"questions":[{"id":"1.3_q1","question":"A method statement is sometimes known as:","options":[{"id":"a","text":"A risk register"},{"id":"b","text":"A safe system of work"},{"id":"c","text":"A safety policy"},{"id":"d","text":"A toolbox talk"}],"correct":"b","explanation":"A method statement describes the safe system of work."},{"id":"1.3_q2","question":"Is there a legal requirement to produce a method statement?","options":[{"id":"a","text":"Yes, under HASAWA 1974"},{"id":"b","text":"Yes, under CDM 2015"},{"id":"c","text":"No, but it is best practice under CDM 2015"},{"id":"d","text":"No, and they are not recommended"}],"correct":"c","explanation":"Method statements are NOT a legal requirement but ARE best practice.","test_tip":"Method statements = best practice. Risk assessments = legal requirement."},{"id":"1.3_q3","question":"What does RAMS stand for?","options":[{"id":"a","text":"Risk Assessment and Management System"},{"id":"b","text":"Risk Assessment and Method Statement"},{"id":"c","text":"Reporting Accidents and Managing Safety"},{"id":"d","text":"Regulations and Methods of Safety"}],"correct":"b","explanation":"RAMS = Risk Assessment and Method Statement."},{"id":"1.3_q4","question":"Why must every worker sign a method statement?","options":[{"id":"a","text":"To prove they can read English"},{"id":"b","text":"To confirm they have read and understood the safe working procedures"},{"id":"c","text":"To confirm they have their CSCS card"},{"id":"d","text":"To agree to work overtime"}],"correct":"b","explanation":"Signing confirms the worker understands the safe procedures."},{"id":"1.3_q5","question":"A method statement should include:","options":[{"id":"a","text":"Workers home addresses"},{"id":"b","text":"A step-by-step guide on how to carry out the task safely"},{"id":"c","text":"The company annual profit figures"},{"id":"d","text":"A list of all workers on site"}],"correct":"b","explanation":"A method statement must include step-by-step safe working instructions."}]}', 0),
('1.4', 1, 14, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 1.4 Quiz","subtitle":"Dynamic Risk Assessment","pass_threshold":0.6,"questions":[{"id":"1.4_q1","question":"When should a dynamic risk assessment be carried out?","options":[{"id":"a","text":"Only before starting work in the morning"},{"id":"b","text":"When conditions on site change or an unexpected hazard appears"},{"id":"c","text":"Only by the site manager at the end of the day"},{"id":"d","text":"Once a month during a safety meeting"}],"correct":"b","explanation":"A dynamic RA is carried out when conditions change."},{"id":"1.4_q2","question":"A dynamic risk assessment is:","options":[{"id":"a","text":"A replacement for a formal written risk assessment"},{"id":"b","text":"A quick, on-the-spot assessment of a changing situation"},{"id":"c","text":"Only needed for work at height"},{"id":"d","text":"Written up and filed before work starts"}],"correct":"b","explanation":"A dynamic RA is done ON THE SPOT.","test_tip":"It does NOT replace a formal written risk assessment."},{"id":"1.4_q3","question":"You notice waterlogged ground after overnight rain. What should you do?","options":[{"id":"a","text":"Carry on as planned"},{"id":"b","text":"Stop and carry out a dynamic risk assessment"},{"id":"c","text":"Wait for the site manager to notice"},{"id":"d","text":"Go home"}],"correct":"b","explanation":"Changed conditions require a dynamic risk assessment."},{"id":"1.4_q4","question":"Who can carry out a dynamic risk assessment?","options":[{"id":"a","text":"Only the health and safety officer"},{"id":"b","text":"Only the site manager"},{"id":"c","text":"Any competent worker who identifies a change in conditions"},{"id":"d","text":"Only someone with a university degree"}],"correct":"c","explanation":"Any competent worker can carry out a dynamic RA."},{"id":"1.4_q5","question":"What is the FIRST thing you should do in a dynamic risk assessment?","options":[{"id":"a","text":"Continue working and assess later"},{"id":"b","text":"Stop the activity and identify what has changed"},{"id":"c","text":"Call the HSE"},{"id":"d","text":"Write a method statement"}],"correct":"b","explanation":"The first step is always to STOP and identify what has changed."}]}', 0),
('1.5', 1, 15, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 1.5 Quiz","subtitle":"Accident Reporting","pass_threshold":0.6,"questions":[{"id":"1.5_q1","question":"Under RIDDOR, who reports a serious workplace accident to the HSE?","options":[{"id":"a","text":"The injured worker"},{"id":"b","text":"The site supervisor"},{"id":"c","text":"The employer"},{"id":"d","text":"The first aider"}],"correct":"c","explanation":"Under RIDDOR, it is the EMPLOYER who reports to the HSE.","test_tip":"Always the EMPLOYER. This is a classic exam trap."},{"id":"1.5_q2","question":"Which is a common cause of FATAL injuries on construction sites?","options":[{"id":"a","text":"Paper cuts"},{"id":"b","text":"Falls from height"},{"id":"c","text":"Sunburn"},{"id":"d","text":"Loud music"}],"correct":"b","explanation":"Falls from height are the most common cause of fatal injuries."},{"id":"1.5_q3","question":"Why is it important to report near misses?","options":[{"id":"a","text":"To get the worker fired"},{"id":"b","text":"To help prevent future accidents by identifying hazards"},{"id":"c","text":"To increase insurance premiums"},{"id":"d","text":"Near misses do not need to be reported"}],"correct":"b","explanation":"Reporting near misses helps identify hazards and prevent future accidents."},{"id":"1.5_q4","question":"Which injuries must be reported under RIDDOR?","options":[{"id":"a","text":"All cuts and bruises"},{"id":"b","text":"Fractures, amputations, and injuries causing over 7 days off work"},{"id":"c","text":"Only injuries requiring hospital treatment"},{"id":"d","text":"Only injuries to visitors"}],"correct":"b","explanation":"RIDDOR requires reporting specified injuries including fractures and 7+ day absences."},{"id":"1.5_q5","question":"What is the consequence for failing to prevent workplace accidents?","options":[{"id":"a","text":"Nothing, accidents are normal"},{"id":"b","text":"Criminal prosecution, fines, compensation claims, and loss of reputation"},{"id":"c","text":"Only a small fine"},{"id":"d","text":"The site must close permanently"}],"correct":"b","explanation":"Consequences include prosecution, fines, compensation claims, and reputation damage."}]}', 0),
('1.6', 1, 16, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 1.6 Quiz","subtitle":"Safety Signs","pass_threshold":0.6,"questions":[{"id":"1.6_q1","question":"A BLUE circular sign with a white symbol means:","options":[{"id":"a","text":"Warning — danger ahead"},{"id":"b","text":"Prohibition — you must NOT do this"},{"id":"c","text":"Mandatory — you MUST do this"},{"id":"d","text":"Safe condition — this way to safety"}],"correct":"c","explanation":"Blue circular signs are mandatory — they tell you something you MUST do."},{"id":"1.6_q2","question":"A red circle with a line through it means:","options":[{"id":"a","text":"Fire equipment is located here"},{"id":"b","text":"You MUST do this"},{"id":"c","text":"You must NOT do this"},{"id":"d","text":"Warning of a hazard"}],"correct":"c","explanation":"Red circle with diagonal line = prohibition."},{"id":"1.6_q3","question":"What shape and colour is a WARNING sign?","options":[{"id":"a","text":"Blue circle"},{"id":"b","text":"Yellow triangle"},{"id":"c","text":"Red square"},{"id":"d","text":"Green rectangle"}],"correct":"b","explanation":"Warning signs are yellow triangles with a black border."},{"id":"1.6_q4","question":"A green rectangular sign with a white running figure means:","options":[{"id":"a","text":"Exercise area"},{"id":"b","text":"Assembly point or emergency exit route"},{"id":"c","text":"No running allowed"},{"id":"d","text":"First aid point"}],"correct":"b","explanation":"Green signs indicate safe conditions — exits and assembly points."},{"id":"1.6_q5","question":"A red square sign with a fire extinguisher symbol indicates:","options":[{"id":"a","text":"No fires allowed"},{"id":"b","text":"Fire exit"},{"id":"c","text":"Fire extinguisher location"},{"id":"d","text":"Flammable materials stored here"}],"correct":"c","explanation":"Red square signs indicate fire equipment locations."}]}', 0),
('1.7', 1, 24, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 1.7 Quiz","subtitle":"Electrical, Fire & PPE","pass_threshold":0.6,"questions":[{"id":"1.7_q1","question":"Which fire extinguisher for an ELECTRICAL fire?","options":[{"id":"a","text":"Water"},{"id":"b","text":"Foam"},{"id":"c","text":"CO2 (Carbon Dioxide)"},{"id":"d","text":"Wet chemical"}],"correct":"c","explanation":"CO2 is safe for electrical fires — it does not conduct electricity.","test_tip":"NEVER use water on electrical fires."},{"id":"1.7_q2","question":"At what noise level must hearing protection be PROVIDED (worker choice)?","options":[{"id":"a","text":"70 dB"},{"id":"b","text":"80 dB"},{"id":"c","text":"85 dB"},{"id":"d","text":"90 dB"}],"correct":"b","explanation":"80 dB = available. 85 dB = mandatory.","test_tip":"80 dB = AVAILABLE. 85 dB = MANDATORY."},{"id":"1.7_q3","question":"You find a damaged electrical cable on site. What do you do?","options":[{"id":"a","text":"Cover it with tape"},{"id":"b","text":"Stop using it, isolate it, and report it"},{"id":"c","text":"Use it carefully"},{"id":"d","text":"Move it out of the way"}],"correct":"b","explanation":"Damaged electrical equipment must be taken out of use immediately."},{"id":"1.7_q4","question":"Which PPE should ALWAYS be worn on a construction site?","options":[{"id":"a","text":"Ear defenders"},{"id":"b","text":"Safety goggles"},{"id":"c","text":"Hard hat and safety boots"},{"id":"d","text":"Dust mask"}],"correct":"c","explanation":"Hard hat and safety boots are the minimum PPE on all construction sites."},{"id":"1.7_q5","question":"Foam extinguisher is most suitable for:","options":[{"id":"a","text":"Electrical fires"},{"id":"b","text":"Metal fires"},{"id":"c","text":"Flammable liquid fires"},{"id":"d","text":"Cooking oil fires"}],"correct":"c","explanation":"Foam extinguishers are most effective on flammable liquid fires (Class B)."}]}', 0),
('1.8', 1, 13, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 1.8 Quiz","subtitle":"Personal Hygiene","pass_threshold":0.6,"questions":[{"id":"1.8_q1","question":"Why is personal hygiene important on a construction site?","options":[{"id":"a","text":"To look professional"},{"id":"b","text":"To prevent illness and absorption of harmful substances"},{"id":"c","text":"Only important in food areas"},{"id":"d","text":"It does not matter"}],"correct":"b","explanation":"Good hygiene prevents illness and harmful substance absorption."},{"id":"1.8_q2","question":"When should you wash your hands on site?","options":[{"id":"a","text":"Only at the end of the day"},{"id":"b","text":"Before eating, drinking, smoking, and after using the toilet"},{"id":"c","text":"Only if they look dirty"},{"id":"d","text":"Twice a day"}],"correct":"b","explanation":"Always wash hands before eating/drinking/smoking and after the toilet."},{"id":"1.8_q3","question":"What welfare facilities must be provided on site?","options":[{"id":"a","text":"TV and comfortable chairs"},{"id":"b","text":"Toilets, washing facilities, drinking water, rest area, and changing facilities"},{"id":"c","text":"Only a portable toilet"},{"id":"d","text":"A canteen with hot meals"}],"correct":"b","explanation":"Employers must provide all basic welfare facilities."},{"id":"1.8_q4","question":"Why NOT eat in areas where hazardous substances are used?","options":[{"id":"a","text":"It makes a mess"},{"id":"b","text":"You could ingest harmful substances from contaminated hands or food"},{"id":"c","text":"It is only a suggestion"},{"id":"d","text":"The food will taste bad"}],"correct":"b","explanation":"Hazardous substances can contaminate your food and be ingested."},{"id":"1.8_q5","question":"Minimum first aid provision on a construction site?","options":[{"id":"a","text":"Hospital within 30 minutes"},{"id":"b","text":"An appointed person and a first aid kit"},{"id":"c","text":"Doctor on site at all times"},{"id":"d","text":"A defibrillator"}],"correct":"b","explanation":"Minimum requirement is an appointed person and first aid kit."}]}', 0),
-- Module 2 (4 lessons)
('2.1', 2, 15, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 2.1 Quiz","subtitle":"Manual Handling","pass_threshold":0.6,"questions":[{"id":"2.1_q1","question":"Manual handling includes which activities?","options":[{"id":"a","text":"Only lifting heavy objects over 25kg"},{"id":"b","text":"Any activity involving bodily force — lifting, pushing, pulling, carrying"},{"id":"c","text":"Only carrying objects up stairs"},{"id":"d","text":"Only activities requiring two or more people"}],"correct":"b","explanation":"Manual handling is ANY bodily force — no minimum weight.","test_tip":"There is NO minimum weight for manual handling."},{"id":"2.1_q2","question":"Most common injury from incorrect manual handling?","options":[{"id":"a","text":"Cuts to the hands"},{"id":"b","text":"Back injuries and musculoskeletal disorders"},{"id":"c","text":"Burns"},{"id":"d","text":"Head injuries"}],"correct":"b","explanation":"Back injuries and MSDs are the most common."},{"id":"2.1_q3","question":"Which is NOT a potential manual handling injury?","options":[{"id":"a","text":"Herniated disc"},{"id":"b","text":"Repetitive strain injury"},{"id":"c","text":"Hearing loss"},{"id":"d","text":"Muscle tears"}],"correct":"c","explanation":"Hearing loss is caused by noise, not manual handling."},{"id":"2.1_q4","question":"Why ensure safe manual handling?","options":[{"id":"a","text":"To save time"},{"id":"b","text":"To prevent injuries leading to long-term health problems"},{"id":"c","text":"To avoid trouble with colleagues"},{"id":"d","text":"Not important if you are strong"}],"correct":"b","explanation":"Safe manual handling prevents chronic pain and disability."},{"id":"2.1_q5","question":"Manual handling injuries can result in:","options":[{"id":"a","text":"Only minor discomfort"},{"id":"b","text":"Long-term chronic pain, inability to work, and permanent disability"},{"id":"c","text":"Nothing serious if you stretch"},{"id":"d","text":"Short-term pain that always goes away"}],"correct":"b","explanation":"Injuries can be severe and permanent."}]}', 0),
('2.2', 2, 17, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 2.2 Quiz","subtitle":"Responsibilities","pass_threshold":0.6,"questions":[{"id":"2.2_q1","question":"Employer FIRST duty for manual handling?","options":[{"id":"a","text":"Provide training"},{"id":"b","text":"Avoid the need for manual handling where reasonably practicable"},{"id":"c","text":"Provide gloves"},{"id":"d","text":"Hire more workers"}],"correct":"b","explanation":"First duty is to AVOID manual handling altogether."},{"id":"2.2_q2","question":"Your responsibility as employee for manual handling?","options":[{"id":"a","text":"Nothing — employer only"},{"id":"b","text":"Follow training, use equipment, and report problems"},{"id":"c","text":"Only lift under 10kg"},{"id":"d","text":"Refuse all manual handling"}],"correct":"b","explanation":"Employees must follow training and report problems."},{"id":"2.2_q3","question":"Materials should be stored:","options":[{"id":"a","text":"As high as possible"},{"id":"b","text":"At convenient height on stable surfaces"},{"id":"c","text":"Wherever there is space"},{"id":"d","text":"Outside the building"}],"correct":"b","explanation":"Store at a height that minimises bending and stretching."},{"id":"2.2_q4","question":"When is mechanical lifting equipment required?","options":[{"id":"a","text":"Only for items over 50kg"},{"id":"b","text":"When the load is too heavy, bulky, or awkward for manual means"},{"id":"c","text":"Only when a forklift is available"},{"id":"d","text":"Never required"}],"correct":"b","explanation":"Use mechanical aids when loads cannot be safely handled manually."},{"id":"2.2_q5","question":"Manual handling task causing you pain — you should:","options":[{"id":"a","text":"Keep going"},{"id":"b","text":"Stop and report to your supervisor"},{"id":"c","text":"Take painkillers"},{"id":"d","text":"Ask someone else without telling anyone"}],"correct":"b","explanation":"Stop immediately and report pain to prevent serious injury."}]}', 0),
('2.3', 2, 15, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 2.3 Quiz","subtitle":"Safe Lifting","pass_threshold":0.6,"questions":[{"id":"2.3_q1","question":"Correct sequence for safe lifting?","options":[{"id":"a","text":"Grab and lift quickly"},{"id":"b","text":"Plan, feet apart, bend knees, straight back, firm grip, smooth lift, keep close"},{"id":"c","text":"Bend at waist, lock knees, twist"},{"id":"d","text":"Lift with arms at arm length"}],"correct":"b","explanation":"This sequence ensures minimum strain and maximum control."},{"id":"2.3_q2","question":"What does TILE stand for?","options":[{"id":"a","text":"Twist, Inspect, Lift, Evaluate"},{"id":"b","text":"Task, Individual, Load, Environment"},{"id":"c","text":"Train, Inform, Lift, Exit"},{"id":"d","text":"Time, Inspection, Loading, Equipment"}],"correct":"b","explanation":"TILE = Task, Individual, Load, Environment.","test_tip":"You MUST know TILE for the exam."},{"id":"2.3_q3","question":"When carrying a load, keep it:","options":[{"id":"a","text":"Above shoulder height"},{"id":"b","text":"At arm length"},{"id":"c","text":"Close to your body at waist height"},{"id":"d","text":"Below knee level"}],"correct":"c","explanation":"Close to body at waist height minimises back strain."},{"id":"2.3_q4","question":"Why avoid twisting when carrying?","options":[{"id":"a","text":"It makes you dizzy"},{"id":"b","text":"It strains your spine and increases back injury risk"},{"id":"c","text":"Not actually a problem"},{"id":"d","text":"Only matters with heavy loads"}],"correct":"b","explanation":"Twisting puts enormous strain on your spine."},{"id":"2.3_q5","question":"Before lifting, check FIRST:","options":[{"id":"a","text":"That your supervisor is watching"},{"id":"b","text":"Weight, shape, condition of load and plan destination"},{"id":"c","text":"That you are wearing gloves"},{"id":"d","text":"The time of day"}],"correct":"b","explanation":"Always assess the load and plan the lift first."}]}', 0),
('2.4', 2, 13, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 2.4 Quiz","subtitle":"Mechanical Aids","pass_threshold":0.6,"questions":[{"id":"2.4_q1","question":"Which is a mechanical aid for manual handling?","options":[{"id":"a","text":"Safety boots"},{"id":"b","text":"Wheelbarrow, sack truck, or pallet truck"},{"id":"c","text":"Hard hat"},{"id":"d","text":"High-vis vest"}],"correct":"b","explanation":"Mechanical aids include wheelbarrows, sack trucks, pallet trucks, forklifts."},{"id":"2.4_q2","question":"Why use mechanical aids?","options":[{"id":"a","text":"More interesting"},{"id":"b","text":"Reduce risk of manual handling injuries"},{"id":"c","text":"HSE requires them for all tasks"},{"id":"d","text":"Cheaper than workers"}],"correct":"b","explanation":"They reduce physical strain and injury risk."},{"id":"2.4_q3","question":"You find a defect in handling equipment:","options":[{"id":"a","text":"Continue using it carefully"},{"id":"b","text":"Stop using it, take out of service, report defect"},{"id":"c","text":"Fix it yourself"},{"id":"d","text":"Report at end of shift"}],"correct":"b","explanation":"Defective equipment must be removed from service immediately."},{"id":"2.4_q4","question":"A sack truck is best for:","options":[{"id":"a","text":"Carrying loose sand"},{"id":"b","text":"Moving heavy or bulky items like bags and boxes"},{"id":"c","text":"Transporting liquids"},{"id":"d","text":"Working at height"}],"correct":"b","explanation":"Sack trucks are designed for heavy, bulky items."},{"id":"2.4_q5","question":"Safe manual handling practices include:","options":[{"id":"a","text":"Rushing to finish quickly"},{"id":"b","text":"Using correct equipment, following procedures, reporting problems"},{"id":"c","text":"Lifting when no one is watching"},{"id":"d","text":"Ignoring minor pain"}],"correct":"b","explanation":"Use right equipment, follow procedures, always report problems."}]}', 0),
-- Module 3 (5 lessons)
('3.1', 3, 13, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 3.1 Quiz","subtitle":"Working at Height","pass_threshold":0.6,"questions":[{"id":"3.1_q1","question":"Working at height applies when:","options":[{"id":"a","text":"You are working above 2 metres"},{"id":"b","text":"You are in any place where you could fall a distance that could cause injury"},{"id":"c","text":"You are working on a roof only"},{"id":"d","text":"You are working above 3 metres"}],"correct":"b","explanation":"There is NO minimum height.","test_tip":"2 metres is the classic WRONG answer in the exam."},{"id":"3.1_q2","question":"Which counts as working at height?","options":[{"id":"a","text":"Walking across a floor with no openings"},{"id":"b","text":"Working next to an unguarded open trench"},{"id":"c","text":"Standing on level ground"},{"id":"d","text":"Sitting in a site office"}],"correct":"b","explanation":"Working near unguarded openings is working at height."},{"id":"3.1_q3","question":"As an employee working at height, you MUST:","options":[{"id":"a","text":"Use any equipment you can find"},{"id":"b","text":"Cooperate with employer policies, use safety equipment correctly, report defects"},{"id":"c","text":"Only follow instructions if you agree"},{"id":"d","text":"Do your own RA without telling anyone"}],"correct":"b","explanation":"Employees must cooperate with safety policies and report defects."},{"id":"3.1_q4","question":"If you find a defect in your safety harness:","options":[{"id":"a","text":"Use it anyway"},{"id":"b","text":"Stop using it, take out of service, report to supervisor immediately"},{"id":"c","text":"Try to repair it yourself"},{"id":"d","text":"Swap it with someone else"}],"correct":"b","explanation":"Defective safety equipment must be removed from use immediately."},{"id":"3.1_q5","question":"Working at height regulations apply to:","options":[{"id":"a","text":"Only roofs and scaffolds"},{"id":"b","text":"Any height including near openings, ladders, scaffolds, roofs, fragile surfaces"},{"id":"c","text":"Only above 2 metres"},{"id":"d","text":"Only when using a ladder"}],"correct":"b","explanation":"The regulations apply at any height where a fall could cause injury."}]}', 0),
('3.2', 3, 12, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 3.2 Quiz","subtitle":"Hazards at Height","pass_threshold":0.6,"questions":[{"id":"3.2_q1","question":"Which is a hazard when working at height?","options":[{"id":"a","text":"Too many workers on site"},{"id":"b","text":"Tools and debris falling onto people below"},{"id":"c","text":"Heavy traffic outside"},{"id":"d","text":"Poor food in the canteen"}],"correct":"b","explanation":"Falling objects are a major hazard when working at height."},{"id":"3.2_q2","question":"A ladder must be:","options":[{"id":"a","text":"Leaned at any angle"},{"id":"b","text":"Secured, on stable ground, at 75-degree angle (1:4 ratio)"},{"id":"c","text":"Used by multiple workers at once"},{"id":"d","text":"Placed on soft ground"}],"correct":"b","explanation":"1 out for every 4 up — the 75° angle rule."},{"id":"3.2_q3","question":"Hazard near overhead power cables?","options":[{"id":"a","text":"Cables too short"},{"id":"b","text":"Electrocution from contact with live cables"},{"id":"c","text":"Cables will fall down"},{"id":"d","text":"No significant hazard"}],"correct":"b","explanation":"Contact with live cables causes electrocution."},{"id":"3.2_q4","question":"Fragile roofs are dangerous because:","options":[{"id":"a","text":"They are ugly"},{"id":"b","text":"A person or heavy object could fall through them"},{"id":"c","text":"They leak"},{"id":"d","text":"Only in old buildings"}],"correct":"b","explanation":"People and objects can fall through fragile roofs."},{"id":"3.2_q5","question":"Internal voids (floor holes) should be:","options":[{"id":"a","text":"Stepped over carefully"},{"id":"b","text":"Covered and marked, or guarded with barriers"},{"id":"c","text":"Left open for ventilation"},{"id":"d","text":"Filled with waste"}],"correct":"b","explanation":"Voids must be covered and clearly marked, or guarded."}]}', 0),
('3.3', 3, 11, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 3.3 Quiz","subtitle":"Controlling Risks at Height","pass_threshold":0.6,"questions":[{"id":"3.3_q1","question":"FIRST control measure for working at height?","options":[{"id":"a","text":"Provide harnesses"},{"id":"b","text":"Avoid working at height if possible"},{"id":"c","text":"Put up warning signs"},{"id":"d","text":"Provide training"}],"correct":"b","explanation":"The hierarchy: AVOID > PREVENT > ARREST > MINIMISE.","test_tip":"AVOID > PREVENT > ARREST > MINIMISE."},{"id":"3.3_q2","question":"Guardrails and edge protection are:","options":[{"id":"a","text":"Fall arrest measures"},{"id":"b","text":"Fall prevention measures"},{"id":"c","text":"PPE"},{"id":"d","text":"Administrative controls"}],"correct":"b","explanation":"Guardrails PREVENT falls. Harnesses ARREST falls.","test_tip":"Prevention before arrest in the hierarchy."},{"id":"3.3_q3","question":"A safety harness is an example of:","options":[{"id":"a","text":"Fall prevention"},{"id":"b","text":"Fall arrest"},{"id":"c","text":"Fall avoidance"},{"id":"d","text":"Administrative control"}],"correct":"b","explanation":"A harness arrests a fall after it has started."},{"id":"3.3_q4","question":"Toe boards on scaffolding are used to:","options":[{"id":"a","text":"Help workers climb"},{"id":"b","text":"Prevent tools and materials from falling off the edge"},{"id":"c","text":"Support the scaffold structure"},{"id":"d","text":"Mark the scaffold as inspected"}],"correct":"b","explanation":"Toe boards stop materials and tools falling off scaffold edges."},{"id":"3.3_q5","question":"Below a work at height area, to protect ground workers:","options":[{"id":"a","text":"Nothing — people should stay away"},{"id":"b","text":"Exclusion zones, barriers, debris netting, or fans"},{"id":"c","text":"Warning signs only"},{"id":"d","text":"Extra hard hats"}],"correct":"b","explanation":"Multiple protective measures should be used below work at height areas."}]}', 0),
('3.4', 3, 11, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 3.4 Quiz","subtitle":"Regulations & Equipment","pass_threshold":0.6,"questions":[{"id":"3.4_q1","question":"Which regulation controls working at height?","options":[{"id":"a","text":"Manual Handling Operations Regulations 1992"},{"id":"b","text":"The Work at Height Regulations 2005"},{"id":"c","text":"COSHH Regulations 2002"},{"id":"d","text":"HASAWA 1974"}],"correct":"b","explanation":"The Work at Height Regulations 2005 specifically control work at height."},{"id":"3.4_q2","question":"Before selecting equipment for working at height:","options":[{"id":"a","text":"Use whatever is available"},{"id":"b","text":"Carry out a risk assessment and select most suitable equipment"},{"id":"c","text":"Always use a scaffold"},{"id":"d","text":"Only use a ladder"}],"correct":"b","explanation":"Equipment selection must be based on risk assessment."},{"id":"3.4_q3","question":"Scaffolding must be inspected:","options":[{"id":"a","text":"Before first use and after any event affecting stability"},{"id":"b","text":"Only once when first erected"},{"id":"c","text":"By any available worker"},{"id":"d","text":"Left in place permanently"}],"correct":"a","explanation":"Scaffolding needs inspection before first use and after events like bad weather."},{"id":"3.4_q4","question":"MEWPs should be operated by:","options":[{"id":"a","text":"Anyone who has watched someone else"},{"id":"b","text":"Only trained and authorised operators"},{"id":"c","text":"Any worker over 21"},{"id":"d","text":"The site manager only"}],"correct":"b","explanation":"Only trained and authorised operators can use MEWPs."},{"id":"3.4_q5","question":"A ladder should be used for work at height:","options":[{"id":"a","text":"For any task regardless of duration"},{"id":"b","text":"Only for short-duration, low-risk tasks"},{"id":"c","text":"Never — ladders are banned"},{"id":"d","text":"Only for tasks lasting more than 30 minutes"}],"correct":"b","explanation":"Ladders are only suitable for short-duration, low-risk work."}]}', 0),
('3.5', 3, 13, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 3.5 Quiz","subtitle":"Practical Safety at Height","pass_threshold":0.6,"questions":[{"id":"3.5_q1","question":"Before using a ladder, you should:","options":[{"id":"a","text":"Just start climbing"},{"id":"b","text":"Inspect it for damage, check it is the right type, and secure it properly"},{"id":"c","text":"Ask someone to hold the bottom"},{"id":"d","text":"Only use it if it is new"}],"correct":"b","explanation":"Always inspect ladders before use and ensure they are suitable and secure."},{"id":"3.5_q2","question":"When working on scaffolding, guardrails should be at least:","options":[{"id":"a","text":"500mm high"},{"id":"b","text":"950mm high"},{"id":"c","text":"1500mm high"},{"id":"d","text":"2000mm high"}],"correct":"b","explanation":"Guardrails on scaffolding should be at least 950mm high."},{"id":"3.5_q3","question":"Who can erect scaffolding?","options":[{"id":"a","text":"Any worker on site"},{"id":"b","text":"Only trained and competent scaffolders"},{"id":"c","text":"Anyone with a CSCS card"},{"id":"d","text":"Only the site manager"}],"correct":"b","explanation":"Scaffolding must only be erected by trained, competent scaffolders."},{"id":"3.5_q4","question":"A scaffold inspection should be recorded:","options":[{"id":"a","text":"Only if a defect is found"},{"id":"b","text":"Every time an inspection takes place, whether defects are found or not"},{"id":"c","text":"Only weekly"},{"id":"d","text":"It does not need to be recorded"}],"correct":"b","explanation":"All scaffold inspections must be recorded as part of legal compliance."},{"id":"3.5_q5","question":"What must you NOT do when working at height?","options":[{"id":"a","text":"Wear a safety harness"},{"id":"b","text":"Throw tools or materials from height"},{"id":"c","text":"Use guardrails"},{"id":"d","text":"Report defects"}],"correct":"b","explanation":"Never throw tools or materials from height — they could injure people below."}]}', 0),
-- Module 4 (5 lessons)
('4.1', 4, 13, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 4.1 Quiz","subtitle":"COSHH & Hazardous Substances","pass_threshold":0.6,"questions":[{"id":"4.1_q1","question":"What does COSHH stand for?","options":[{"id":"a","text":"Control of Safety and Health Hazards"},{"id":"b","text":"Control of Substances Hazardous to Health"},{"id":"c","text":"Code of Safety and Health in Housing"},{"id":"d","text":"Construction of Safe and Healthy Habitats"}],"correct":"b","explanation":"COSHH = Control of Substances Hazardous to Health."},{"id":"4.1_q2","question":"Routes hazardous substances can enter the body?","options":[{"id":"a","text":"Through the eyes only"},{"id":"b","text":"Inhalation, ingestion, absorption through skin, or injection"},{"id":"c","text":"Only by swallowing"},{"id":"d","text":"Only by breathing in"}],"correct":"b","explanation":"All four routes: inhalation, ingestion, skin absorption, injection."},{"id":"4.1_q3","question":"Silicosis is caused by:","options":[{"id":"a","text":"Working in cold temperatures"},{"id":"b","text":"Breathing in silica dust over time"},{"id":"c","text":"Contact with wet cement"},{"id":"d","text":"Loud noise"}],"correct":"b","explanation":"Silicosis is caused by long-term exposure to silica dust."},{"id":"4.1_q4","question":"HAVS is caused by:","options":[{"id":"a","text":"Cold weather"},{"id":"b","text":"Regular use of vibrating tools such as drills and grinders"},{"id":"c","text":"Manual handling"},{"id":"d","text":"Working at height"}],"correct":"b","explanation":"HAVS is caused by regular use of vibrating tools."},{"id":"4.1_q5","question":"Dermatitis on construction sites is commonly caused by:","options":[{"id":"a","text":"Working in the sun"},{"id":"b","text":"Contact with cement, solvents, or other irritant substances"},{"id":"c","text":"Not drinking enough water"},{"id":"d","text":"Wearing safety boots"}],"correct":"b","explanation":"Dermatitis is caused by contact with irritant substances like cement."}]}', 0),
('4.2', 4, 11, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 4.2 Quiz","subtitle":"Drugs, Alcohol & Storage","pass_threshold":0.6,"questions":[{"id":"4.2_q1","question":"A worker under the influence of alcohol on site is a risk because:","options":[{"id":"a","text":"They might be late"},{"id":"b","text":"Their judgement and reactions are impaired, risking themselves and others"},{"id":"c","text":"They might upset colleagues"},{"id":"d","text":"It only affects their own work"}],"correct":"b","explanation":"Impaired judgement puts everyone at risk on site."},{"id":"4.2_q2","question":"Flammable materials should be stored:","options":[{"id":"a","text":"Anywhere convenient"},{"id":"b","text":"In a designated, ventilated area away from ignition sources"},{"id":"c","text":"In the site office"},{"id":"d","text":"Near the main entrance"}],"correct":"b","explanation":"Flammable materials need proper ventilated storage away from ignition sources."},{"id":"4.2_q3","question":"Why read a COSHH data sheet before using a chemical?","options":[{"id":"a","text":"To check the price"},{"id":"b","text":"To understand hazards, required PPE, and emergency procedures"},{"id":"c","text":"It is optional"},{"id":"d","text":"Only if you haven not used chemicals before"}],"correct":"b","explanation":"COSHH data sheets contain critical safety information."},{"id":"4.2_q4","question":"Incompatible chemicals should be:","options":[{"id":"a","text":"Stored together to save space"},{"id":"b","text":"Stored separately to prevent dangerous reactions"},{"id":"c","text":"Mixed to neutralise each other"},{"id":"d","text":"Left outside"}],"correct":"b","explanation":"Incompatible chemicals can cause dangerous reactions if mixed."},{"id":"4.2_q5","question":"If you suspect a colleague is under the influence on site:","options":[{"id":"a","text":"Ignore it"},{"id":"b","text":"Report it to your supervisor immediately"},{"id":"c","text":"Give them coffee"},{"id":"d","text":"Cover for them"}],"correct":"b","explanation":"Report it immediately — it is a safety risk to everyone."}]}', 0),
('4.3', 4, 12, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 4.3 Quiz","subtitle":"Asbestos, Hygiene & PPE","pass_threshold":0.6,"questions":[{"id":"4.3_q1","question":"If you discover suspected asbestos:","options":[{"id":"a","text":"Remove it yourself"},{"id":"b","text":"STOP work, do NOT disturb it, leave the area, report to supervisor"},{"id":"c","text":"Cover it with plastic and carry on"},{"id":"d","text":"Sweep it up and put in a bin bag"}],"correct":"b","explanation":"STOP, don''t touch, leave, report. Only licensed contractors remove asbestos.","test_tip":"STOP, DON''T TOUCH, LEAVE, REPORT."},{"id":"4.3_q2","question":"Which disease is linked to asbestos exposure?","options":[{"id":"a","text":"Diabetes"},{"id":"b","text":"Mesothelioma"},{"id":"c","text":"Arthritis"},{"id":"d","text":"HAVS"}],"correct":"b","explanation":"Mesothelioma is specifically caused by asbestos exposure."},{"id":"4.3_q3","question":"PPE should be considered:","options":[{"id":"a","text":"The first line of defence"},{"id":"b","text":"The last resort when other controls are not practicable"},{"id":"c","text":"Optional if the worker feels they don''t need it"},{"id":"d","text":"Only necessary for supervisors"}],"correct":"b","explanation":"PPE is the LAST resort in the hierarchy of controls.","test_tip":"PPE is the LAST resort, not the first."},{"id":"4.3_q4","question":"PPE for handling hazardous chemicals?","options":[{"id":"a","text":"Just safety boots"},{"id":"b","text":"Chemical-resistant gloves, goggles, and respiratory protection as per COSHH assessment"},{"id":"c","text":"A hard hat only"},{"id":"d","text":"Hi-vis vest"}],"correct":"b","explanation":"PPE must match the specific hazards identified in the COSHH assessment."},{"id":"4.3_q5","question":"The three types of asbestos commonly found are:","options":[{"id":"a","text":"Red, yellow, and green"},{"id":"b","text":"White (chrysotile), brown (amosite), and blue (crocidolite)"},{"id":"c","text":"Dry, wet, and frozen"},{"id":"d","text":"Natural, synthetic, and recycled"}],"correct":"b","explanation":"White (chrysotile), brown (amosite), and blue (crocidolite) are the three main types."}]}', 0),
('4.4', 4, 11, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 4.4 Quiz","subtitle":"Noise & Vibration","pass_threshold":0.6,"questions":[{"id":"4.4_q1","question":"At what noise level is hearing protection MANDATORY?","options":[{"id":"a","text":"70 dB"},{"id":"b","text":"80 dB"},{"id":"c","text":"85 dB"},{"id":"d","text":"90 dB"}],"correct":"c","explanation":"At 85 dB hearing protection is mandatory. At 80 dB it must be available.","test_tip":"80 dB = available. 85 dB = mandatory."},{"id":"4.4_q2","question":"Long-term exposure to excessive noise can cause:","options":[{"id":"a","text":"Back injuries"},{"id":"b","text":"Permanent hearing loss and tinnitus"},{"id":"c","text":"Skin rashes"},{"id":"d","text":"Eye damage"}],"correct":"b","explanation":"Noise-induced hearing loss is permanent and irreversible."},{"id":"4.4_q3","question":"HAVS can be prevented by:","options":[{"id":"a","text":"Wearing gloves at all times"},{"id":"b","text":"Limiting exposure time to vibrating tools and using low-vibration equipment"},{"id":"c","text":"Working only in warm weather"},{"id":"d","text":"Taking painkillers"}],"correct":"b","explanation":"Limiting exposure time and using low-vibration tools are key prevention measures."},{"id":"4.4_q4","question":"Symptoms of HAVS include:","options":[{"id":"a","text":"Headaches"},{"id":"b","text":"Tingling, numbness, and loss of grip strength in fingers"},{"id":"c","text":"Hearing loss"},{"id":"d","text":"Back pain"}],"correct":"b","explanation":"HAVS causes nerve and blood vessel damage in the fingers and hands."},{"id":"4.4_q5","question":"Employers must monitor noise levels on site when:","options":[{"id":"a","text":"Workers complain about noise"},{"id":"b","text":"There is reason to believe levels may exceed 80 dB"},{"id":"c","text":"Only in enclosed spaces"},{"id":"d","text":"Never — it is the worker''s responsibility"}],"correct":"b","explanation":"Employers must assess and monitor noise levels that may exceed action values."}]}', 0),
('4.5', 4, 9, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 4.5 Quiz","subtitle":"Occupational Health","pass_threshold":0.6,"questions":[{"id":"4.5_q1","question":"Health surveillance is required when:","options":[{"id":"a","text":"Workers ask for it"},{"id":"b","text":"Workers are exposed to specific health risks identified in risk assessments"},{"id":"c","text":"Only for new starters"},{"id":"d","text":"Only for workers over 50"}],"correct":"b","explanation":"Health surveillance is needed when risk assessments identify specific health risks."},{"id":"4.5_q2","question":"Leptospirosis (Weil''s disease) can be contracted from:","options":[{"id":"a","text":"Asbestos fibres"},{"id":"b","text":"Contact with water or soil contaminated by rat urine"},{"id":"c","text":"Loud noise"},{"id":"d","text":"Vibrating tools"}],"correct":"b","explanation":"Leptospirosis comes from contaminated water or soil — cover cuts and wash hands."},{"id":"4.5_q3","question":"Sun exposure on construction sites can cause:","options":[{"id":"a","text":"Hearing loss"},{"id":"b","text":"Skin cancer and heat exhaustion"},{"id":"c","text":"HAVS"},{"id":"d","text":"Respiratory problems"}],"correct":"b","explanation":"UV radiation from sun exposure can cause skin cancer."},{"id":"4.5_q4","question":"Signs of heat exhaustion include:","options":[{"id":"a","text":"Cold, pale skin"},{"id":"b","text":"Heavy sweating, weakness, nausea, and dizziness"},{"id":"c","text":"Shivering"},{"id":"d","text":"Hearing loss"}],"correct":"b","explanation":"Heat exhaustion symptoms include heavy sweating, weakness, and dizziness."},{"id":"4.5_q5","question":"To prevent occupational health problems, employers should:","options":[{"id":"a","text":"Ignore worker complaints"},{"id":"b","text":"Carry out risk assessments, provide control measures, and offer health surveillance"},{"id":"c","text":"Only provide PPE"},{"id":"d","text":"Tell workers to be more careful"}],"correct":"b","explanation":"A comprehensive approach including risk assessment, controls, and surveillance is needed."}]}', 0),
-- Module 5 (4 lessons)
('5.1', 5, 13, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 5.1 Quiz","subtitle":"Plant & Machinery Dangers","pass_threshold":0.6,"questions":[{"id":"5.1_q1","question":"How can moving plant cause injuries?","options":[{"id":"a","text":"By making too much noise"},{"id":"b","text":"Crushing, being struck, entanglement, or overturning"},{"id":"c","text":"By being too slow"},{"id":"d","text":"By using too much fuel"}],"correct":"b","explanation":"Moving plant causes injuries through crushing, strikes, entanglement, and overturning."},{"id":"5.1_q2","question":"Why are safeguards (exclusion zones, banksmen) important around plant?","options":[{"id":"a","text":"To make the site look professional"},{"id":"b","text":"To prevent unauthorised people entering danger zones and alert workers"},{"id":"c","text":"They are optional extras"},{"id":"d","text":"To slow down work"}],"correct":"b","explanation":"Safeguards prevent unauthorised access to danger zones around plant."},{"id":"5.1_q3","question":"Before approaching moving plant:","options":[{"id":"a","text":"Walk up and knock on the cab"},{"id":"b","text":"Make eye contact with the operator and wait for acknowledgement"},{"id":"c","text":"Shout to get attention"},{"id":"d","text":"Just walk past"}],"correct":"b","explanation":"NEVER approach until the operator has acknowledged you.","test_tip":"Drivers have blind spots — always make eye contact first."},{"id":"5.1_q4","question":"A banksman''s role is to:","options":[{"id":"a","text":"Handle money"},{"id":"b","text":"Guide plant operators safely during reversing and in confined areas"},{"id":"c","text":"Drive the plant"},{"id":"d","text":"Inspect plant at end of day"}],"correct":"b","explanation":"Banksmen guide operators safely, especially during reversing."},{"id":"5.1_q5","question":"Excavator operators have blind spots. This means:","options":[{"id":"a","text":"The operator needs glasses"},{"id":"b","text":"There are areas around the machine the operator cannot see"},{"id":"c","text":"The machine needs new mirrors"},{"id":"d","text":"Workers should only stand behind"}],"correct":"b","explanation":"Blind spots are areas the operator cannot see, making nearby workers at risk."}]}', 0),
('5.2', 5, 12, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 5.2 Quiz","subtitle":"Control Measures & Warning Signs","pass_threshold":0.6,"questions":[{"id":"5.2_q1","question":"Method statement for plant operations should include:","options":[{"id":"a","text":"Operator personal details"},{"id":"b","text":"Step-by-step safe operation procedures, exclusion zones, emergency procedures"},{"id":"c","text":"Manufacturer share price"},{"id":"d","text":"Only insurance details"}],"correct":"b","explanation":"Method statements must cover safe procedures and emergency plans."},{"id":"5.2_q2","question":"Control measure for working near plant?","options":[{"id":"a","text":"Wearing casual clothes"},{"id":"b","text":"Exclusion zones, banksmen, and hi-vis clothing"},{"id":"c","text":"Working as fast as possible"},{"id":"d","text":"Only working in daylight"}],"correct":"b","explanation":"Exclusion zones, banksmen, and hi-vis are key control measures."},{"id":"5.2_q3","question":"A yellow warning triangle near plant indicates:","options":[{"id":"a","text":"Plant is for sale"},{"id":"b","text":"A specific hazard workers should be aware of"},{"id":"c","text":"Plant is broken"},{"id":"d","text":"Authorised personnel only"}],"correct":"b","explanation":"Yellow triangles warn of specific hazards."},{"id":"5.2_q4","question":"How can risks from plant be ELIMINATED?","options":[{"id":"a","text":"Working faster"},{"id":"b","text":"Physically separating pedestrians from plant operations"},{"id":"c","text":"Giving everyone hard hats"},{"id":"d","text":"Risks can never be eliminated"}],"correct":"b","explanation":"Physical separation eliminates the risk completely."},{"id":"5.2_q5","question":"Why wear hi-vis clothing near plant?","options":[{"id":"a","text":"Fashion requirement"},{"id":"b","text":"To be visible to plant operators, reducing risk of being struck"},{"id":"c","text":"Only needed at night"},{"id":"d","text":"Protects against chemicals"}],"correct":"b","explanation":"Hi-vis makes workers visible to plant operators and other workers."}]}', 0),
('5.3', 5, 11, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 5.3 Quiz","subtitle":"Electrical Tools & Equipment","pass_threshold":0.6,"questions":[{"id":"5.3_q1","question":"Before using a portable electrical tool, you should:","options":[{"id":"a","text":"Just plug it in and start"},{"id":"b","text":"Visually inspect it for damage, check it has been PAT tested, and check the cable"},{"id":"c","text":"Only check if it is new"},{"id":"d","text":"Ask a colleague if it works"}],"correct":"b","explanation":"Always visually inspect electrical tools before use."},{"id":"5.3_q2","question":"What voltage should portable tools be on a construction site?","options":[{"id":"a","text":"240V mains"},{"id":"b","text":"110V reduced voltage through a transformer"},{"id":"c","text":"12V battery only"},{"id":"d","text":"Any voltage is fine"}],"correct":"b","explanation":"110V is the standard for portable tools on construction sites — it reduces electrocution risk."},{"id":"5.3_q3","question":"A yellow 110V plug indicates:","options":[{"id":"a","text":"The tool is broken"},{"id":"b","text":"The tool is suitable for use on construction sites"},{"id":"c","text":"The tool needs charging"},{"id":"d","text":"The tool is for indoor use only"}],"correct":"b","explanation":"Yellow plugs indicate 110V tools suitable for construction site use."},{"id":"5.3_q4","question":"If an RCD (residual current device) trips:","options":[{"id":"a","text":"Reset it and carry on"},{"id":"b","text":"Stop work, do not use the equipment, and report the fault"},{"id":"c","text":"Bypass the RCD"},{"id":"d","text":"It does not matter"}],"correct":"b","explanation":"An RCD trip indicates a fault — stop, report, and do not use the equipment."},{"id":"5.3_q5","question":"Trailing cables on site should be:","options":[{"id":"a","text":"Left where they fall"},{"id":"b","text":"Routed safely, protected from damage, and not creating trip hazards"},{"id":"c","text":"Covered with carpet"},{"id":"d","text":"Only used in dry weather"}],"correct":"b","explanation":"Cables must be routed safely and protected from damage."}]}', 0),
('5.4', 5, 10, 'quiz_card', '{"type":"lesson_quiz","title":"Lesson 5.4 Quiz","subtitle":"Vehicles & Transport on Site","pass_threshold":0.6,"questions":[{"id":"5.4_q1","question":"Site vehicles must be driven by:","options":[{"id":"a","text":"Anyone with a car driving licence"},{"id":"b","text":"Only trained and authorised operators"},{"id":"c","text":"Anyone over 18"},{"id":"d","text":"Only the site manager"}],"correct":"b","explanation":"Only trained and authorised operators can drive site vehicles."},{"id":"5.4_q2","question":"Pedestrians and vehicles should be:","options":[{"id":"a","text":"Allowed to share the same routes"},{"id":"b","text":"Separated where possible using designated walkways and vehicle routes"},{"id":"c","text":"Only separated at night"},{"id":"d","text":"It does not matter"}],"correct":"b","explanation":"Separating pedestrians and vehicles is a key safety measure on site."},{"id":"5.4_q3","question":"When a vehicle is reversing on site:","options":[{"id":"a","text":"Pedestrians should walk behind it"},{"id":"b","text":"A banksman should guide the driver and keep pedestrians clear"},{"id":"c","text":"The driver does not need help"},{"id":"d","text":"Only the driver is at risk"}],"correct":"b","explanation":"Banksmen guide reversing vehicles and keep pedestrians clear."},{"id":"5.4_q4","question":"Speed limits on construction sites are important because:","options":[{"id":"a","text":"They save fuel"},{"id":"b","text":"They reduce the risk of collisions with pedestrians and other vehicles"},{"id":"c","text":"They are only suggestions"},{"id":"d","text":"They only apply to cars"}],"correct":"b","explanation":"Speed limits protect workers on foot from vehicle collisions."},{"id":"5.4_q5","question":"Before starting a vehicle or plant at the start of a shift:","options":[{"id":"a","text":"Just start the engine"},{"id":"b","text":"Carry out pre-use checks including lights, brakes, mirrors, and horn"},{"id":"c","text":"Only check the fuel level"},{"id":"d","text":"It does not need checking if it worked yesterday"}],"correct":"b","explanation":"Pre-use checks ensure the vehicle is safe to operate."}]}', 0);